import { useContext } from "react";
import { makeStyles } from "@material-ui/core/styles";
import Table from "@material-ui/core/Table";
import TableBody from "@material-ui/core/TableBody";
import TableCell from "@material-ui/core/TableCell";
import TableHead from "@material-ui/core/TableHead";
import TableRow from "@material-ui/core/TableRow";
import Paper from "@material-ui/core/Paper";
import Typography from "@material-ui/core/Typography";
import Link from "next/link";
import { ShopContext } from "../../contexts/ShopContext";

const styles = theme => ({
    paper: {
        padding: theme.spacing(2),
        marginBottom: theme.spacing(6)
    },
    item: {
        display: "flex",
        alignItems: "center"
    },
    img: {
        width: "40%",
        verticalAlign: "center",
        [theme.breakpoints.down("xs")]: {
            display: "none"
        }
    },
    name: {
        marginLeft: theme.spacing(2),
        cursor: "pointer"
    },
    numberCell: {
        width: "20%"
    }
});

const useStyles = makeStyles(styles);

const tableHead = ["Unit Price", "Quantity", "Total Price"];

const Confirm = () => {
    const classes = useStyles();

    const { cart, totalItems, totalAmounts } = useContext(ShopContext);

    return (
        <Paper elevation={0} className={classes.paper}>
            <Typography variant="h5">Confirm Checkout?</Typography>
            <Table className={classes.table}>
                <TableHead>
                    <TableRow>
                        <TableCell>Items</TableCell>
                        {tableHead.map(el => (
                            <TableCell align="right">{el}</TableCell>
                        ))}
                    </TableRow>
                </TableHead>
                <TableBody>
                    {cart.map((el, i) => (
                        <TableRow key={i}>
                            <TableCell
                                className={classes.itemCell}
                                component="th"
                                scope="row"
                            >
                                <div className={classes.item}>
                                    <img
                                        className={classes.img}
                                        src={el.imgSrc}
                                    />
                                    <Link
                                        href={`/shop/[id]`}
                                        as={`/shop/${el.id}`}
                                    >
                                        <Typography className={classes.name}>
                                            {el.name}
                                        </Typography>
                                    </Link>
                                </div>
                            </TableCell>
                            <TableCell
                                align="right"
                                className={classes.numberCell}
                            >
                                $ {el.price}
                            </TableCell>
                            <TableCell
                                align="right"
                                className={classes.numberCell}
                            >
                                {el.quantity}
                            </TableCell>
                            <TableCell
                                align="right"
                                className={classes.numberCell}
                            >
                                $ {el.price * el.quantity}
                            </TableCell>
                        </TableRow>
                    ))}
                </TableBody>
            </Table>
        </Paper>
    );
};

export default Confirm;
