export default themes => ({
    slideShow: {
        width: "100%",
        height: "90vh",
        position: "relative"
    },
    img: {
        width: "100%",
        height: "100%",
        objectFit: "cover",
        objectPosition: "50% 20%"
    },
    wording: {
        color: "white",
        textAlign: "center",
        fontWeight: "700",
        position: "absolute",
        top: "50%",
        left: "50%",
        transform: "translate(-50%, -50%)"
    }
});
